import { createFeatureSelector, createReducer, createSelector, on } from "@ngrx/store";
import { FeatureTypes } from "src/app/state/state-features-types";
import { ProductActions } from "./product.actions";
const productInitialState = {
  showProductCode: true,
  currentProctId: 0,
  products: []
};
export const productReducer = createReducer(productInitialState, on(ProductActions.ToggleCodeVisibility, state => {
  return Object.assign(Object.assign({}, state), {
    showProductCode: !state.showProductCode
  });
}));
const productsSelector = createFeatureSelector(FeatureTypes.Products);
export const showProductCode = createSelector(productsSelector, productState => {
  return productState.showProductCode;
});
